use std::io;pub fn bucle_siempre () {        loop {        println!("Escribe algo (o 'salir' para salir):");        let mut entrada = String::new();        io::stdin().read_line(&mut entrada).expect("Error al leer la l√≠nea");        let entrada = entrada.trim();        if entrada == "salir" {            println!("Saliendo...");            break        } else {            println!("Escribiste: {}", entrada);        }    }}